def initUsrId = newObject.owner._ref.toString();
initUsrId = initUsrId.substring(1, initUsrId.length() - 1);
println "InitUserID = " + initUsrId;
def sourceUsrId = newObject.source._ref.toString();
sourceUsrId = sourceUsrId.substring(1, sourceUsrId.length() - 1);
println "SourceUserID = " + sourceUsrId;
def ticketOwner = openidm.read(initUsrId, null, ["_id"]);
def notificationSource = openidm.read(sourceUsrId, null, ["_id"]);

def params = [
    "_key": "iotNotificationProcess",
    "initiatorUser": ticketOwner._id,
    "ticketId": newObject._id,
    "triggerValue": newObject.triggerValue,
    "triggerName": newObject.triggerName,
    "respondedOn": newObject.respondedOn,
    "triggeredOn": newObject.triggeredOn,
    "notes": newObject.notes,
    "sourceUsr": notificationSource._id
]

openidm.create("workflow/processinstance", null, params);
